 [:script "
 document.addEventListener('DOMContentLoaded', function() {
 const grid = document.querySelector('.day-column');
 if(!grid) return;

 let isDragging = false;
 let startY = 0;
 let currentY = 0;
 const selection = document.createElement('div');
 selection.id = 'selection-box';
 grid.appendChild(selection);

 grid.addEventListener('mousedown', function(e) {
 if(e.target.classList.contains('calendar-event')) return; // Ne selektuj preko postojecih
 isDragging = true;
 const rect = grid.getBoundingClientRect();
 startY = e.clientY - rect.top + grid.scrollTop;

 // Snap to 15 mins (15px)
 startY = Math.floor(startY / 15) * 15;

 selection.style.top = startY + 'px';
 selection.style.height = '0px';
 selection.style.display = 'block';
 });

 grid.addEventListener('mousemove', function(e) {
 if (!isDragging) return;
 const rect = grid.getBoundingClientRect();
 let y = e.clientY - rect.top + grid.scrollTop;

 let height = y - startY;
 if(height < 15) height = 15; // Minimum 15 min

 selection.style.height = height + 'px';
 });

 grid.addEventListener('mouseup', function(e) {
 if (!isDragging) return;
 isDragging = false;

 // Izraƒçunaj vreme na osnovu piksela (1px = 1min)
 // Ali posto smo stavili 60px = 1h, onda je 1px = 1minuta.

 const height = parseInt(selection.style.height);
 const startMin = startY;
 const endMin = startY + height;

 // Konvertuj u HH:mm
 const formatTime = (minutes) => {
 let h = Math.floor(minutes / 60);
 let m = Math.floor(minutes % 60);
 return (h < 10 ? '0'+h : h) + ':' + (m < 10 ? '0'+m : m);
 };

 const sTime = formatTime(startMin);
 const eTime = formatTime(endMin);

 // Sakrij selekciju
 selection.style.display = 'none';

 // Pozovi HTMX da otvori modal sa ovim podacima
 // Treba nam i datum koji je trenutno izabran (uzimamo iz URL-a ili DOM-a)
 const urlParams = new URLSearchParams(window.location.search);
 const date = urlParams.get('date') || 'today';

 htmx.ajax('GET', '/modal/add?date=' + date + '&start=' + sTime + '&end=' + eTime, '#modal-container');
 });
 });
 "]]))

;; --- MODAL KOMPONENTA ---

(defn add-activity-modal [request]
 (let [params (:params request)
 date-str (:date params)
 start-str (:start params) ;; "14:30"
 end-str (:end params)] ;; "16:00"

 {:status 200
 :headers {"Content-Type" "text/html"}
 :body (html5
 [:div.modal-overlay {:_ "on click self.remove()"}
 [:div.modal {:onclick "event.stopPropagation()"}
 [:h3 "Nova Aktivnost: " date-str]
 [:form {:hx-post "/activity"
 ;; Posle uspe≈°nog upisa, osve≈æi celu stranicu da se vidi nova aktivnost
 :onsubmit "setTimeout(()=>window.location.reload(), 100)"}

 ;; Skriveni podaci koje je korisnik "prevukao" mi≈°em
 [:input {:type "hidden" :name "date" :value date-str}]
 [:input {:type "hidden" :name "username" :value "Micko"}] ;; TODO: Session

 [:div.form-group
 [:label "Vreme"]
 [:div {:style "display:flex; gap:10px"}
 [:input {:type "time" :name "start" :value start-str :readonly true}]
 [:input {:type "time" :name "end" :value end-str :readonly true}]]]

 [:div.form-group
 [:label "Tip Aktivnosti"]
 [:select {:name "activity-key"}
 [:option {:value "training"} "üèãÔ∏è Trening"]
 [:option {:value "study"} "üìö Uƒçenje"]
 [:option {:value "coding"} "üíª Programiranje"]
 [:option {:value "work"} "üíº Posao"]
 [:option {:value "hobby"} "üé® Hobi"]]]

 [:div.form-group
 [:label "Intenzitet (1-5)"]
 [:input {:type "range" :min "1" :max "5" :value "3" :name "intensity"}]]

 [:button.btn {:type "submit"} "Saƒçuvaj"]
 [:button.btn.btn-cancel {:type "button" :onclick "document.querySelector('.modal-overlay').remove()"} "Otka≈æi"]]]])}))

;; --- HANDLER ZA ƒåUVANJE ---

(defn save-activity-handler [request]
 (let [params (:params request)
 username (:username params)
 date-str (:date params)
 start-time (:start params)
 end-time (:end params)
 activity-key (keyword (:activity-key params))
 intensity (Integer/parseInt (:intensity params))

 ;; Izraƒçunaj trajanje
 s (LocalTime/parse start-time)
 e (LocalTime/parse end-time)
 duration (.toMinutes (Duration/between s e))

 ;; Povezivanje datuma i vremena da bi se dobio pravi Date objekat za bazu
 ;; (Ako tvoja funkcija add-activity! prima java.util.Date)
 local-date (parse-date date-str)
 local-dt (.atTime local-date s)
 final-date (java.util.Date/from (.toInstant (.atZone local-dt (ZoneId/of "Europe/Belgrade"))))]

 ;; Zovi tvoj API
 ;; Ako tvoj API prima duration, super. Ako prima start/end, prilagodi.
 ;; Ovde pretpostavljam da prima duration kao ranije.
 (project.api/log-activity! username activity-key duration intensity)

 {:status 200 :body "OK"}))

;; --- PLANNER PAGE ---

(defn planner-page [request]
 (let [date-str (get-in request [:params :date])
 current-date (parse-date date-str)
 prev-date (format-date (get-prev-day current-date))
 next-date (format-date (get-next-day current-date))

 ;; Dohvati aktivnosti za TAJ dan
 ;; (Ovde koristi≈° tvoju funkciju get-daily-report ili sliƒçno)
 ;; Ovo je primer strukture koju treba da vrati≈°:
 daily-data (project.api/get-daily-report "Micko" current-date)
 activities (:activities daily-data)]

 {:status 200
 :headers {"Content-Type" "text/html"}
 :body (common-layout
 [:div
 ;; HEADER SA DATUMIMA (Skrol levo-desno)
 [:div.planner-header
 [:a.date-nav-btn {:href (str "?date=" prev-date)} "‚óÄ"]
 [:span.current-date-title (nice-date current-date)]
 [:a.date-nav-btn {:href (str "?date=" next-date)} "‚ñ∂"]]

 ;; TELO KALENDARA
 [:div.calendar-body
 ;; Sati
 [:div.time-labels
 (for [h (range 24)]
 [:div.time-label (str h ":00")])]

 ;; Grid za crtanje
 [:div.day-column
 ;; Renderuj postojeƒáe aktivnosti
 (for [act activities]
 (let [;; Tvoj report vraƒáa :activity/start-time kao Date/Instant
 ;; Moramo izraƒçunati minute od ponoƒái da bi znali 'top' poziciju
 inst (:activity/start-time act)
 start-min (instant->minutes-from-midnight inst)
 duration (:activity/duration act)

 top start-min ;; 1min = 1px (jednostavna matematika)
 height duration]

 [:div.calendar-event
 {:style (str "top: " top "px; height: " height "px;")}
 [:strong (:activity/type act)]
 [:br]
 (str duration " min")]))]]]
 :planner)}))

;; --- RUTER I START ---

(def app
 (ring/ring-handler
 (ring/router
 [
 ["/" {:get (fn [_] {:status 302 :headers {"Location" "/planner"} :body ""})}]
 ["/planner" {:get planner-page}]
 ["/modal/add" {:get add-activity-modal}]
 ["/activity" {:post save-activity-handler}]
 ["/leaderboard" {:get (fn [_] {:status 200 :body "Ovde ide leaderboard"})}]
 ]
 {:data {:middleware [parameters/parameters-middleware]}})
 (ring/create-default-handler)))

(defonce server (atom nil))

(defn start-server []
 (when-not @server
 (reset! server (jetty/run-jetty #'app {:port 3000 :join? false}))
 (println "Server na http://localhost:3000")))

(defn stop-server []
 (when @server
 (.stop @server)
 (reset! server nil)
 (println "Stop.")))", :offset 14630, :ns "web.server"} {:command "(def app
 (ring/ring-handler
 (ring/router
 [
 ["/" {:get (fn [_] {:status 302 :headers {"Location" "/planner"} :body ""})}]
 ["/planner" {:get planner-page}]
 ["/modal/add" {:get add-activity-modal}]
 ["/activity" {:post save-activity-handler}]
 ["/leaderboard" {:get (fn [_] {:status 200 :body "Ovde ide leaderboard"})}]
 ]
 {:data {:middleware [parameters/parameters-middleware]}})
 (ring/create-default-handler)))", :offset 470, :ns "web.server"} {:command "(defn parse-date [date-str]
 (if (or (empty? date-str) (= "today" date-str))
 (LocalDate/now)
 (try (LocalDate/parse date-str)
 (catch Exception _ (LocalDate/now)))))

