(def app
 (ring/ring-handler
 (ring/router
 [
 ["/" {:get (fn [_] {:status 302 :headers {"Location" "/planner"} :body ""})}]
 ["/planner" {:get planner-page}]
 ["/modal/add" {:get add-activity-modal}]
 ["/activity" {:post save-activity-handler}]
 ["/leaderboard" {:get (fn [_] {:status 200 :body "Ovde ide leaderboard"})}]
 ]
 {:data {:middleware [parameters/parameters-middleware]}})
 (ring/create-default-handler)))

(defonce server (atom nil))

(defn start-server []
 (when-not @server
 (reset! server (jetty/run-jetty #'app {:port 3000 :join? false}))
 (println "Server na http://localhost:3000")))

(defn stop-server []
 (when @server
 (.stop @server)
 (reset! server nil)
 (println "Stop.")))
(defn leaderboard-table [data]
 [:div
 [:h2 "Rank list "]
 [:table
 [:thead
 [:tr
 [:th "Rank"]
 [:th "Username"]
 [:th "XP"]
 [:th "Delta"]]]
 [:tbody
 (for [user data]
 [:tr
 [:td (:rank user)]
 [:td (:user/username user)]
 [:td (:user/xp user)]
 [:td (let [d (:delta user)]
 (cond
 (pos? d) [:span {:style "color:green"} (str "‚ñ≤ " d)]
 (neg? d) [:span {:style "color:red"} (str "‚ñº " (Math/abs d))]
 :else [:span {:style "color:gray"} "-"]))]

 ])]
 ]]
 )
