 {:status 200
 :headers {"Content-Type" "text/html"}
 :body (html5 [:div [:h2 {:style "color: blue"} "Server radi"]])})

#_(def app
 (ring/ring-handler
 (ring/router [
 ["/" {:get home-page}]
 ["/klik" {:post click-handler}]
 ])
 (ring/create-default-handler)))


