 [:input#title {:name "title" :type "text" :hx-post "/title" :hx-change "change" :hx-sync "closest form:abort"}]
 [:input#duration {:name "duration" :type "text" :hx-post "/duration" :hx-change "change" :hx-sync "closest form:abort"}]
 [:button {:type "submit"} "Submit"]]
