 [:nav-sidebar
 [:div.logo "BeBetter"]
 [:a.nav-link {:hx-get "/planner" }] [:span "Planner"]]
 [:main.main-content
 content]
 [:footer.footer
 [:p "¬© 2024 BeBetter - Clojure + HTMX"]]]

